<script setup>
import { ref, onMounted } from "vue";
import bus from "../utils/bus";
let yeslog = ref("0");
let statab = ref("0");
let inpval = ref("0");
let inpvalt = ref("0");
let userbla = ref("0");
let userusdtbla = ref("0");
let istcshow = ref("0");
let stashow = ref("0");
let ethshow = ref("0");
let staimgy = ref(
  "data:image/png;base64,"
);
let ethxx = ref({
  APR: "3.02%",
  Exchange: "1 wBETH = 1.02666 ezETH",
  Transaction: "~ $6.79",
  Reward: "10%",
});
let stab = ref("wBETH");
let stalist = ref([
  {
    dbimg:
      "data:image/png;base64,",
    dbtxt: "wBETH",
  },
  {
    dbimg: "/img/stETH.d147b0fc.svg",
    dbtxt: "stETH",
  },
  {
    dbimg: "/img/ETHb.4213cf87.svg",
    dbtxt: "ETH",
  },
]);
onMounted(() => {
  bus.$on("qbbalance", (val) => {
    if (val != "") {
      yeslog.value = "1";
      userbla.value = Number(val).toFixed(2);
    }
  });
  bus.$on("qbusdtbalance", (val) => {
    if (val != "") {
      yeslog.value = "1";
      userusdtbla.value = Number(val).toFixed(2);
    }
  });
  bus.$on("logout", (val) => {
    if (val == "out") {
      yeslog.value = "0";
    }
  });
  if (localStorage.getItem("xhladd")) {
    yeslog.value = "1";
  }
  if (localStorage.getItem("xhlbalance")) {
    userbla.value = Number(localStorage.getItem("xhlbalance")).toFixed(2);
  }
  if (localStorage.getItem("xhlusdtbalance")) {
    userusdtbla.value = Number(localStorage.getItem("xhlusdtbalance")).toFixed(
      2
    );
  }
});
let changetab = (str) => {
  statab.value = str;
};
let stabalance = () => {
  inpval.value = Number(userbla.value).toFixed(2);
};
let statbalance = () => {
  inpvalt.value = Number(userusdtbla.value).toFixed(2);
};
let unsinput = () => {
  inpvalt.value = inpvalt.value.replace(/[^\d.]/g, "");
};
let sinput = () => {
  inpval.value = inpval.value.replace(/[^\d.]/g, "");
};
let statcs = (str) => {
  istcshow.value = str;
};
let isstop = () => {
  console.log("add");
};
let changebs = (item) => {
  staimgy.value = item.dbimg;
  stab.value = item.dbtxt;
  stashow.value = "0";
};
let stalists = (str) => {
  if (str == stashow.value) {
    stashow.value = "0";
  } else {
    stashow.value = "1";
  }
};
let changeeth = (str) => {
  if (str == ethshow.value) {
    ethshow.value = "0";
  } else {
    ethshow.value = "1";
  }
};
</script>

